    // 将图片URL转换为DataURL
    convertImageToDataURL(url) {
      return new Promise((resolve, reject) => {
        // 首先尝试直接fetch获取图片（更可靠的方法）
        fetch(url, {
          mode: 'cors',
          headers: {
            'Content-Type': 'image/jpeg',
            'Access-Control-Allow-Origin': '*'
          },
          credentials: 'include'
        }).then(response => {
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          return response.blob();
        }).then(blob => {
          const reader = new FileReader();
          reader.onloadend = () => {
            console.log('使用fetch成功获取图片并转换为DataURL');
            resolve(reader.result);
          };
          reader.onerror = () => {
            console.error('FileReader读取失败');
            reject(new Error('读取图片失败'));
          };
          reader.readAsDataURL(blob);
        }).catch(fetchError => {
          console.warn('fetch方法失败，尝试使用canvas方法:', fetchError);

          // 如果fetch失败，尝试使用canvas方法
          try {
            // 创建一个新的图片对象
            const img = new Image();

            // 设置CORS以允许跨域图片
            img.crossOrigin = 'anonymous';
            img.onload = function () {
              try {
                // 创建canvas元素
                const canvas = document.createElement('canvas');
                canvas.width = this.width || 800;
                canvas.height = this.height || 600;

                // 绘制图片到canvas
                const ctx = canvas.getContext('2d');
                ctx.drawImage(this, 0, 0, canvas.width, canvas.height);
                try {
                  // 转换为DataURL
                  const dataURL = canvas.toDataURL('image/jpeg', 0.9);
                  console.log('使用canvas成功转换图片');
                  resolve(dataURL);
                } catch (toDataURLError) {
                  console.error('canvas toDataURL失败:', toDataURLError);
                  reject(toDataURLError);
                }
              } catch (canvasError) {
                console.error('canvas处理失败:', canvasError);
                reject(canvasError);
              }
            };
            img.onerror = function () {
              console.error('图片加载失败');
              reject(new Error('图片加载失败'));
            };

            // 设置超时
            setTimeout(() => {
              if (!img.complete) {
                img.src = '';
                reject(new Error('图片加载超时'));
              }
            }, 10000);

            // 开始加载图片
            img.src = url;
          } catch (error) {
            reject(error);
          }
        });
      });
