        // 这里只是模拟上传，实际项目中应该发送到服务器
        const reader = new FileReader();
        reader.onload = event => {
          this.articleForm.coverImage = event.target.result;
        };
        reader.readAsDataURL(file);
      }
      // 重置input，允许重复上传同一文件
      e.target.value = '';
    },
    // 处理内容图片上传
    handleImageUpload(e) {
      const file = e.target.files[0];
      if (file) {
        // 模拟上传
        const reader = new FileReader();
        reader.onload = event => {
          // 将图片添加到媒体列表
          this.articleForm.media.push({
            id: Date.now(),
            type: 'image',
            url: event.target.result,
            name: file.name
          });

          // 同时在编辑器中插入图片
          if (this.$refs.editorContent) {
            this.$refs.editorContent.focus();
            this.formatText('insertHTML', `<img src="${event.target.result}" alt="${file.name}" class="max-w-full h-auto">`);
          }
        };
        reader.readAsDataURL(file);
      }
      // 重置input，允许重复上传同一文件
      e.target.value = '';
    },
    // 处理视频上传
    handleVideoUpload(e) {
      const file = e.target.files[0];
      if (file) {
        // 模拟上传
        this.articleForm.media.push({
          id: Date.now(),
          type: 'video',
          url: '#',
          name: file.name
        });
      }
      e.target.value = '';
    },
