    filterArticlesByCategory(category) {
      this.filteredCategory = category;
      this.currentView = 'articles';
      // 滚动到顶部
      window.scrollTo(0, 0);
    },
    // 清除分类筛选
    clearCategoryFilter() {
      this.filteredCategory = null;
    },
    // 查看文章详情
    viewArticle(id) {
      this.currentArticle = this.articles.find(article => article.id === id);
      if (this.currentArticle) {
        // 查找相关文章（同分类，排除当前文章）
        this.relatedArticles = this.articles.filter(article => article.category === this.currentArticle.category && article.id !== id).slice(0, 3);
        this.currentView = 'articleDetail';
        // 滚动到顶部
        window.scrollTo(0, 0);
      }
    },
    // 获取分类颜色
    getCategoryColor(category) {
      const colorMap = {
        '前端开发': 'bg-blue-500',
        '后端开发': 'bg-green-500',
        '数据分析': 'bg-yellow-500',
        '人工智能': 'bg-purple-500',
        '设计': 'bg-pink-500',
        '移动开发': 'bg-red-500'
      };
      return colorMap[category] || 'bg-gray-500';
    },
    // 格式化日期
    formatDate(dateString) {
      const date = new Date(dateString);
      return date.toLocaleDateString('zh-CN', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    },
    // 为输入框格式化日期
    formatDateForInput(date) {
      const year = date.getFullYear();
      const month = String(date.getMonth() + 1).padStart(2, '0');
      const day = String(date.getDate()).padStart(2, '0');
      return `${year}-${month}-${day}`;
    },
    // 格式化相对时间
    formatRelativeTime(timestamp) {
      const now = new Date();
      const past = new Date(timestamp);
      const diffMs = now - past;
      const diffMins = Math.floor(diffMs / (1000 * 60));
      const diffHours = Math.floor(diffMins / 60);
      const diffDays = Math.floor(diffHours / 24);
      if (diffMins < 1) {
        return '刚刚';
      } else if (diffMins < 60) {
        return `${diffMins}分钟前`;
      } else if (diffHours < 24) {
        return `${diffHours}小时前`;
      } else if (diffDays < 30) {
        return `${diffDays}天前`;
      } else {
        return this.formatDate(timestamp);
      }
    },
    // 根据ID获取文章标题
    getArticleTitleById(id) {
      const article = this.articles.find(a => a.id === id);
      return article ? article.title : '未知文章';
    },
    // 编辑文章
    editArticle(id) {
      const article = this.articles.find(a => a.id === id);
      if (article) {
        this.articleForm = {
          id: article.id,
          title: article.title,
          category: article.category,
          tagsStr: article.tags.join(','),
          coverImage: article.coverImage,
          content: article.content,
          status: 'published',
          publishedDate: this.formatDateForInput(new Date(article.publishedDate)),
          media: []
        };
        this.isEditingArticle = true;
        this.showArticleEditor = true;

        // 将内容设置到编辑器
        if (this.$refs.editorContent) {
          this.$refs.editorContent.innerHTML = article.content;
        }
      }
    },
    // 重置文章表单
    resetArticleForm() {
      this.articleForm = {
        id: null,
        title: '',
        category: '',
        tagsStr: '',
        coverImage: '',
        content: '',
        status: 'published',
        publishedDate: this.formatDateForInput(new Date()),
        media: []
      };

      // 清空编辑器
      if (this.$refs.editorContent) {
        this.$refs.editorContent.innerHTML = '<p>开始撰写您的文章内容...</p>';
      }
    },
    // 处理封面图片上传
    handleCoverImageUpload(e) {
      const file = e.target.files[0];
      if (file) {
        // 这里只是模拟上传，实际项目中应该发送到服务器
        const reader = new FileReader();
        reader.onload = event => {
          this.articleForm.coverImage = event.target.result;
        };
        reader.readAsDataURL(file);
      }
    },
    // 触发图片上传
    triggerImageUpload() {
      document.getElementById('imageUpload').click();
    },
    // 触发视频上传
    triggerVideoUpload() {
      document.getElementById('videoUpload').click();
    },
    // 触发音频上传
    triggerAudioUpload() {
      document.getElementById('audioUpload').click();
    },
    // 处理图片上传
    handleImageUpload(e) {
      const file = e.target.files[0];
      if (file) {
        // 模拟上传
        const reader = new FileReader();
        reader.onload = event => {
          this.articleForm.media.push({
            id: Date.now(),
            type: 'image',
            url: event.target.result,
            name: file.name
          });
        };
        reader.readAsDataURL(file);
      }
      // 重置input，允许重复上传同一文件
      e.target.value = '';
    },
    // 处理视频上传
    handleVideoUpload(e) {
      const file = e.target.files[0];
      if (file) {
        // 模拟上传
        this.articleForm.media.push({
          id: Date.now(),
          type: 'video',
          url: '#',
          name: file.name
        });
      }
      e.target.value = '';
    },
    // 处理音频上传
    handleAudioUpload(e) {
      const file = e.target.files[0];
      if (file) {
        // 模拟上传
        this.articleForm.media.push({
          id: Date.now(),
          type: 'audio',
          url: '#',
          name: file.name
        });
      }
      e.target.value = '';
    },
    // 移除媒体
    removeMedia(id) {
      this.articleForm.media = this.articleForm.media.filter(media => media.id !== id);
    },
    // 编辑分类
    editCategory(id) {
      const category = this.categories.find(c => c.id === id);
      if (category) {
        this.categoryForm = {
          ...category
        };
        this.isEditingCategory = true;
        this.showCategoryModal = true;
      }
    },
    // 重置分类表单
    resetCategoryForm() {
      this.categoryForm = {
        id: null,
        name: '',
        description: '',
        icon: 'fa-file-text-o'
      };
      this.isEditingCategory = false;
    },
    // 删除文章
    deleteArticle(id) {
      const article = this.articles.find(a => a.id === id);
      if (article) {
        this.deleteItemId = id;
        this.deleteItemType = 'article';
        this.deleteConfirmMessage = `确定要删除文章《${article.title}》吗？此操作不可撤销。`;
        this.showDeleteConfirm = true;
      }
    },
    // 删除分类
    deleteCategory(id) {
      const category = this.categories.find(c => c.id === id);
      if (category) {
        this.deleteItemId = id;
        this.deleteItemType = 'category';
        this.deleteConfirmMessage = `确定要删除分类《${category.name}》吗？该分类下的${category.count}篇文章将受到影响。`;
        this.showDeleteConfirm = true;
      }
    },
    // 确认删除
    confirmDelete() {
      if (this.deleteItemType === 'article') {
        this.articles = this.articles.filter(a => a.id !== this.deleteItemId);
      } else if (this.deleteItemType === 'category') {
        this.categories = this.categories.filter(c => c.id !== this.deleteItemId);
      }
      this.showDeleteConfirm = false;
    }
  }
