        this.$refs.editorContent.focus();

        // 尝试执行命令
        try {
          // 对于formatBlock命令，需要特殊处理
          if (command === 'formatBlock' && value) {
            document.execCommand('formatBlock', false, `<${value}>`);
          } else {
            document.execCommand(command, false, value);
          }
          this.updateContent();
        } catch (error) {
          console.error('编辑器命令执行失败:', error);
        }
      }
