    handleImageUpload(e) {
      const file = e.target.files[0];
      if (file) {
        // 模拟上传
        const reader = new FileReader();
        reader.onload = event => {
          this.articleForm.media.push({
            id: Date.now(),
            type: 'image',
            url: event.target.result,
            name: file.name
          });
        };
        reader.readAsDataURL(file);
      }
      // 重置input，允许重复上传同一文件
      e.target.value = '';
    },
    // 处理视频上传
    handleVideoUpload(e) {
      const file = e.target.files[0];
      if (file) {
        // 模拟上传
        this.articleForm.media.push({
          id: Date.now(),
          type: 'video',
          url: '#',
          name: file.name
        });
      }
      e.target.value = '';
    },
    // 处理音频上传
    handleAudioUpload(e) {
      const file = e.target.files[0];
      if (file) {
        // 模拟上传
        this.articleForm.media.push({
          id: Date.now(),
          type: 'audio',
          url: '#',
          name: file.name
        });
      }
      e.target.value = '';
    },
    // 移除媒体
    removeMedia(id) {
      this.articleForm.media = this.articleForm.media.filter(media => media.id !== id);
    },
    // 更新文章
    async updateArticle() {
      if (!this.articleForm.id) {
        alert('请先编辑文章');
        return;
      }

      // 转换标签字符串为数组
      this.articleForm.tags = this.articleForm.tagsStr.split(',').map(tag => tag.trim());

      // 发送PUT请求更新文章
      try {
        const url = `http://localhost:5000/api/articles/${this.articleForm.id}`;
        const response = await fetch(url, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(this.articleForm)
        });
        if (!response.ok) {
          throw new Error('Failed to update article');
        }

        // 重置表单
        this.resetArticleForm();
        this.showArticleEditor = false;
        alert('文章更新成功');
      } catch (error) {
        console.error('Error updating article:', error);
        alert('更新文章失败，请稍后重试');
      }
    },
    // 发布文章
    async postArticle() {
      if (!this.articleForm.title.trim()) {
        alert('请输入文章标题');
        return;
      }
      if (!this.articleForm.content.trim()) {
        alert('请输入文章内容');
        return;
      }
      if (!this.articleForm.category) {
        alert('请选择文章分类');
        return;
      }

      // 转换标签字符串为数组
      this.articleForm.tags = this.articleForm.tagsStr.split(',').map(tag => tag.trim());

      // 发送POST请求创建文章
      try {
        const url = 'http://localhost:5000/api/articles/post';
        const response = await fetch(url, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            ...this.articleForm,
            author_id: 'aaa'
          })
        });
        const data = await response.json();
        console.log(data);
        if (!response.ok) {
          throw new Error('Failed to post article');
        }

        // 重置表单
        this.resetArticleForm();
        this.showArticleEditor = false;
        alert('文章发布成功');
      } catch (error) {
        console.error('Error posting article:', error);
        alert('发布文章失败，请稍后重试');
      }
    },
    // 编辑分类
    editCategory(id) {
      const category = this.categories.find(c => c.id === id);
      if (category) {
        this.categoryForm = {
          ...category
        };
        this.isEditingCategory = true;
        this.showCategoryModal = true;
      }
    },
    // 重置分类表单
    resetCategoryForm() {
      this.categoryForm = {
        id: null,
        name: '',
        description: '',
        icon: 'fa-file-text-o'
      };
      this.isEditingCategory = false;
    },
    // API调用 - 更新分类
    async updateCategory() {
      if (!this.categoryForm.name.trim()) {
        alert('请输入分类名称');
        return;
      }
      if (!this.categoryForm.description.trim()) {
        alert('请输入分类描述');
        return;
      }
      try {
        const url = `http://localhost:5000/api/categories/${this.categoryForm.id}`;
        const response = await fetch(url, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(this.categoryForm)
        });
        if (!response.ok) {
          throw new Error('Failed to update category');
        }

        // 更新本地分类列表
        const updatedCategory = await response.json();
        const index = this.categories.findIndex(c => c.id === this.categoryForm.id);
        if (index !== -1) {
          this.categories.splice(index, 1, updatedCategory);
        }
        this.resetCategoryForm();
        this.showCategoryModal = false;
        alert('分类更新成功');
      } catch (error) {
        console.error('Error updating category:', error);
        alert('更新分类失败，请稍后重试');
      }
    },
    // API调用 - 创建分类
    async createCategory() {
      if (!this.categoryForm.name.trim()) {
        alert('请输入分类名称');
        return;
      }
      if (!this.categoryForm.description.trim()) {
        alert('请输入分类描述');
        return;
      }
      try {
        const url = 'http://localhost:5000/api/categories/create';
        const response = await fetch(url, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            ...this.categoryForm,
            count: 0,
            lastUpdated: new Date().toISOString().split('T')[0]
          })
        });
        if (!response.ok) {
          throw new Error('Failed to create category');
        }

        // 添加新分类到本地列表
        const newCategory = await response.json();
        this.categories.push(newCategory);
        this.resetCategoryForm();
        this.showCategoryModal = false;
        alert('分类创建成功');
      } catch (error) {
        console.error('Error creating category:', error);
        // 如果API调用失败，使用本地创建
        const localCategory = {
          ...this.categoryForm,
          id: Date.now(),
          count: 0,
          lastUpdated: new Date().toISOString().split('T')[0]
        };
        this.categories.push(localCategory);
        this.resetCategoryForm();
        this.showCategoryModal = false;
        alert('分类已创建（本地模式）' + error);
      }
    },
    // 删除文章
    deleteArticle(id) {
      const article = this.articles.find(a => a.id === id);
      if (article) {
        this.deleteItemId = id;
        this.deleteItemType = 'article';
        this.deleteConfirmMessage = `确定要删除文章《${article.title}》吗？此操作不可撤销。`;
        this.showDeleteConfirm = true;
      }
    },
    // 删除分类
    deleteCategory(id) {
      const category = this.categories.find(c => c.id === id);
      if (category) {
        this.deleteItemId = id;
        this.deleteItemType = 'category';
        this.deleteConfirmMessage = `确定要删除分类《${category.name}》吗？该分类下的${category.count}篇文章将受到影响。`;
        this.showDeleteConfirm = true;
      }
    },
    // 确认删除
    async confirmDelete() {
      try {
        if (this.deleteItemType === 'article') {
          // 调用API删除文章
          const url = `http://localhost:5000/api/articles/${this.deleteItemId}`;
          const response = await fetch(url, {
            method: 'DELETE',
            headers: {
              'Content-Type': 'application/json'
            }
          });
          if (!response.ok) {
            throw new Error('Failed to delete article');
          }

          // 更新本地数据
          this.articles = this.articles.filter(a => a.id !== this.deleteItemId);
          alert('文章删除成功');
        } else if (this.deleteItemType === 'category') {
          this.categories = this.categories.filter(c => c.id !== this.deleteItemId);
        }
      } catch (error) {
        console.error(`Error deleting ${this.deleteItemType}:`, error);
        alert(`删除${this.deleteItemType === 'article' ? '文章' : '分类'}失败，请稍后重试`);
      } finally {
        this.showDeleteConfirm = false;
      }
    }
  }
};
